insert into Product(ProductId,ProductName,Descript,Price,ImagePath,ManuDate,BrandId) values (1,'Nike Air Max 90 SE','Nike Air Max 90 SE เจริญรอยตามต้นแบบรุ่น OG พร้อมๆ กับเป็นก้าวย่างใหม่ไปสู่อนาคต ไม่มีคู่ไหนที่ทะยานเท่า สบายเท่า และผ่านการพิสูจน์มาเท่ากับคู่นี้รองเท้าผลิตด้วยวัสดุรีไซเคิลอย่างน้อย 20% ตามน้ำหนัก โดยมีการใช้ไม้ก๊อก 9% ในพื้นรองเท้าชั้นนอกลายวาฟเฟิลอันเป็นเอกลักษณ์ พร้อมเย็บติดส่วนหุ้มชั้นนอกด้วยเดนิมรีไซเคิล 100% และกราฟิกลายพืชพรรณแบบปักมาพร้อมกับระบบลดแรงกระแทก Max Air ที่เพิ่มความสบายให้กับการเดินทาง',5000,'test','2021-03-02-',1);
insert into Product(ProductId,ProductName,Descript,Price,ImagePath,ManuDate,BrandId) values (2,'Nike Air Force 1 ''07 EMB','ความเบิกบานดำรงอยู่ใน Nike Air Force 1 ''07 EMB รองเท้าบาสรุ่นไอคอนที่ใส่ลูกเล่นฉบับ Sports Specialties ให้กับสิ่งที่คุณรู้จักดีที่สุด ไม่ว่าจะเป็นส่วนหุ้มชั้นนอกแบบเย็บติด สีสันสะดุดตา และความวิบวับที่ลงตัวซึ่งทำให้คุณเปล่งประกายSwoosh แบบมีเท็กซ์เจอร์และแผงด้านหลังผสานกับตัวอักษร “NIKE” แบบปักเป็นการใช้ลูกเล่นคารวะหมวกทีมโปรดของคุณ ขณะที่การลงแบรนดิ้ง Sports Specialties อันสดใหม่ก็เป็นการแต่งแต้มลุคจากยุค 90 ที่กลายเป็นตำนานขึ้นมาได้ด้วยวัฒนธรรมฮิปฮอป',3800,'test','2020-12-30',1);
