insert into Product(ProductId,ProductName,Descript,Price,ImagePath,ManuDate,BrandId) values (13,'Nike Air Max 95 SE','Nike Air Max 95 SE รองเท้าที่นำแรงบันดาลใจมาจากร่างกายมนุษย์และความสวยงามของสไตล์ลู่วิ่งยุค 90 มีส่วนผสมของความสบายอันเหลือเชื่อ สไตล์ที่ใครก็ต้องเหลียวมอง และดีไซน์ที่เป็นมิตรต่อสิ่งแวดล้อมแผงด้านข้างอันดูดีมีเอกลักษณ์จะตัดเย็บด้วยเดนิมและตาข่ายรีไซเคิล ซึ่งถือว่าในตอนนี้ได้มีส่วนประกอบเป็นวัสดุรีไซเคิลอย่างน้อย 20% ตามน้ำหนักแล้วกราฟิกไม้ก๊อกแบบปักที่ลิ้นรองเท้ายกย่องให้กับพืชที่ใช้ในดีไซน์Air แบบมองเห็นได้ที่ส้นและปลายเท้าลดแรงกระแทกในทุกย่างก้าว',6100,'test','2021-05-05',1);
insert into Product(ProductId,ProductName,Descript,Price,ImagePath,ManuDate,BrandId) values (14,'Nike Air Force 1 ''07 LV8','Nike Air Force 1 ''07 LV8 ปรับโฉมรองเท้ารุ่นออริจินัลปี 82 ซึ่งได้แรงบันดาลใจจากบาสเก็ตบอลด้วยวัสดุรีไซเคิลอย่างน้อย 20% ตามน้ำหนักและพื้นรองเท้าชั้นนอกแบบผสมไม้ก๊อก โดยเพิ่มลูกเล่นแบบธรรมชาติให้กับรุ่นคลาสสิกที่พวกเราทุกคนต่างรู้จักและหลงรักด้วยลวดลายต้นทับทิมที่ประกอบด้วยสีย้อมจากพืช ดีไซน์พืชพรรณแบบปัก และภาพอินโฟกราฟิกทางวิทยาศาสตร์',3800,'test','2021-05-01',1);
insert into Product(ProductId,ProductName,Descript,Price,ImagePath,ManuDate,BrandId) values (15,'Air Jordan 1 Mid','รองเท้า Air Jordan 1 Mid ได้แรงบันดาลใจจาก AJ1 รุ่นแรก และเป็นการให้โอกาสแฟนของ Jordan เรโทรได้ตามรอยความยิ่งใหญ่ สีสันสดใสแต่งแต้มวัสดุคลาสสิกส่วนต่างๆ ให้สะอาดตา พร้อมเติมความสดใหม่บางอย่างลงในดีไซน์ที่คุ้นเคย',4200,'test','2021-03-02',1);
insert into Product(ProductId,ProductName,Descript,Price,ImagePath,ManuDate,BrandId) values (16,'Nike Waffle One','Nike Waffle One นำทุกองค์ประกอบที่คุณรักที่สุดในรองเท้าวิ่งระดับตำนานของ Nike มาใช้อย่างสมดุลกันผ่านนวัตกรรมใหม่ๆ เพื่อสร้างลุคใหม่ให้กับแฟรนไชส์ Waffle ระดับไอคอนส่วนคาดหลังส้นพลาสติกทนทานโฉมใหม่ช่วยส่งพลัง ขณะที่ตาข่ายโปร่งใสผสมกับหนังกลับงามหยดก็ช่วยให้คุณพร้อมลุยหน้าร้อนมาพร้อมพื้นรองเท้าชั้นกลางสปริงตัวได้ดีแบบใหม่ที่โดดเด่นด้วยทรงหนาคลาสสิก และพื้นรองเท้าชั้นนอกลายวาฟเฟิลปรับโฉมใหม่ที่ให้ระดับการรองรับและการยึดเกาะที่ไม่น่าเชื่อจนกว่าจะได้ลอง',3600,'test','2021-05-06',1);
insert into Product(ProductId,ProductName,Descript,Price,ImagePath,ManuDate,BrandId) values (17,'ULTRABOOST 21 X PARLEY SHOES','Raise your own bar. That''s what adidas did when we updated the legendary Ultraboost running shoes with even more energy-returning benefits than before. The responsive Boost midsole, foot-hugging upper and grippy yet flexible sole are still here. Now, a Linear Energy Push system gives extra propulsion to each step with increased forefoot and midfoot stiffness. This version is made using recycled materials, part of adidas commitment to help end plastic waste. This product is made with Primeblue, a high-performance recycled material made in part with Parley Ocean Plastic. 50% of the upper is textile, 92% of the textile is Primeblue yarn. No virgin polyester.',6500,'test','2021-05-01',2);
